import React, { useState, useEffect } from "react";
import questions from "./data";
import "./App.css"; // Stil dosyasını içe aktar

const App = () => {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [score, setScore] = useState(0);

  useEffect(() => {
    setCurrentQuestionIndex(0);
  }, []);

  const checkAnswer = (selectedOption) => {
    const currentQuestion = questions[currentQuestionIndex];
    if (selectedOption === currentQuestion.correctAnswer) {
      setScore(score + 10);
    }
    setCurrentQuestionIndex(currentQuestionIndex + 1);
  };

  const options = ['A', 'B', 'C', 'D']; // Soru şıklarını temsil eden bir dizi

  return (
    <div className="container"> {/* className ile CSS sınıflarını uygula */}
      {currentQuestionIndex < questions.length ? (
        <div>
          <h1>10 Sorulu Soru Programı</h1>
          <div className="question-container"> {/* Soru bileşenini sarmala */}
            <h2>{questions[currentQuestionIndex].question}</h2>
            <ul className="options-list">
              {questions[currentQuestionIndex].options.map((option, index) => (
                <li key={index}>
                  <button onClick={() => checkAnswer(option)}>{options[index]}. {option}</button>
                </li>
              ))}
            </ul>
          </div>
        </div>
      ) : (
        <div>
          <h2>Tebrikler! Tüm soruları tamamladınız.</h2>
          <h2>Toplam Skor: {score}</h2>
        </div>
      )}
    </div>
  );
};

export default App;
