// App.js

import React, { useState, useEffect } from "react";
import questions from "./data";
import "./App.css"; // Stil dosyasını içe aktar
import Score from "./Score";

const App = () => {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [userAnswers, setUserAnswers] = useState(Array(questions.length).fill('')); // Kullanıcının verdiği cevapları saklar
  const [showScore, setShowScore] = useState(false);

  useEffect(() => {
    setCurrentQuestionIndex(0);
  }, []);

  const checkAnswer = (selectedOption) => {
    const currentQuestion = questions[currentQuestionIndex];
    if (selectedOption === currentQuestion.correctAnswer) {
      setScore(score + 10);
    }
    const updatedUserAnswers = [...userAnswers]; // Kullanıcının cevaplarını güncellemek için kopyasını oluştur
    updatedUserAnswers[currentQuestionIndex] = selectedOption; // Kullanıcının cevabını güncelle
    setUserAnswers(updatedUserAnswers);
  };

  const goToPreviousQuestion = () => {
    if (currentQuestionIndex > 0) {
      setCurrentQuestionIndex(currentQuestionIndex - 1);
    }
  };

  const goToNextQuestion = () => {
    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    }
  };

  const finishQuiz = () => {
    setShowScore(true);
  };

  const closeScore = () => {
    setShowScore(false);
  };

  return (
    <div className="container">
      <h1>10 Sorulu Soru Programı</h1>
      <div className="question-container">
        <h2>{questions[currentQuestionIndex].question}</h2>
        <ul className="options-list">
          {questions[currentQuestionIndex].options.map((option, index) => (
            <li key={index}>
              <button onClick={() => checkAnswer(option)}>{String.fromCharCode(65 + index)}. {option}</button>
            </li>
          ))}
        </ul>
      </div>
      <div className="navigation-buttons">
        <button onClick={goToPreviousQuestion} disabled={currentQuestionIndex === 0}>Önceki Soru</button>
        <button onClick={goToNextQuestion} disabled={currentQuestionIndex === questions.length - 1}>Sonraki Soru</button>
        <button onClick={finishQuiz} disabled={currentQuestionIndex < questions.length - 1}>Bitir</button>
      </div>
      {showScore && <Score score={score} onClose={closeScore} />}
    </div>
  );
};

export default App;
